package com.syntax.class09;

public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=10;i>=1;i--) {
			for (int j=1; j<=10; j++) {
			System.out.print(" "+j);
		}
			System.out.println();
	}
		System.out.println("===========================================");
		///////////////////////////////////////
		for(int r=5; r>=1; r--) {
			for(int c=1; c<=4; c++) {
				System.out.print(r+" ");
			}
			System.out.println();
		}
	}
}
